<?php
function hex_to_utf8($string) {
	static $hex_to_chr = array();
	if (empty($hex_to_chr)) {
		mb_internal_encoding("UTF-8");
		/*$alphabet = '0123456789abcdef';
		for ($i = 0; $i < 16; $i++) {
			for ($j = 0; $j < 16; $j++) {
				$hex_to_chr[$alphabet[$i] . $alphabet[$j]] = chr(16 * $i + $j);
			}
		}*/
		$hex_to_chr = unserialize(base64_decode(''));
	}
	$string = str_split($string, 2);
	$nof_bytes = count($string);
	$return = '';
	for ($i = 0; $i < $nof_bytes; $i++) {
		$return .= $hex_to_chr[$string[$i]];
	}
	return $return;
}
